    <script type="text/javascript">
        $(function() {
            $(window).scroll(function() {
                if ($(this).scrollTop() > 100) {
                    $('.totop').css('visibility', 'visible');
                } else {
                    $('.totop').css('visibility', 'hidden');
                }
            });

            $('#clicktotop').click(function(event) {
                $('html, body').animate({
                    scrollTop: 0
                }, 1000);
            });

            $('.socialCircle-item').click(function(event) {
                var type = $(this).data('type');
                var url = '';
                var thisurl = window.location.href;

                // https://simplesharebuttons.com
                if (type == 'google') {
                    url = 'https://plus.google.com/share?url=' + thisurl + '';
                } else if (type == 'facebook') {
                    url = 'http://www.facebook.com/sharer.php?u=' + thisurl + '';
                } else if (type == 'twitter') {
                    url = 'https://twitter.com/intent/tweet?url=' + thisurl + '';
                }

                window.open(url, 'popup', 'width=500,height=500,scrollbars=no,resizable=no');
            });

        });
    </script>
    <script>
        var top_tmp = 143;
        $(window).on("scroll touchmove ready", function() {
            var windowTop = $(window).scrollTop();
            if (windowTop > top_tmp) {
                $("#govmenu_display").addClass('animated fadeIn p-fixed');
                $("div.detail-tab-menu").css('margin', '0 auto');
            } else {
                $("#govmenu_display").removeClass('animated fadeIn p-fixed');
                $("div.detail-tab-menu").css('margin', '0 auto');
            }
        });
    </script>
    <script>
        var btn = $('#button');
        $(window).scroll(function() {
            if ($(window).scrollTop() > 300) {
                btn.addClass('show');
                document.getElementById("fb").style.bottom = "100px";
            } else {
                btn.removeClass('show');
                document.getElementById("fb").style.bottom = "25px";
            }
        });

        btn.on('click', function(e) {
            e.preventDefault();
            $('html, body').animate({
                scrollTop: 0
            }, 1000);
        });
    </script>

